import{_ as a,c as e,o as t,a4 as r}from"./chunks/framework.ZkTbufXI.js";const f=JSON.parse('{"title":"11408通关之路","description":"","frontmatter":{"title":"11408通关之路","date":"2024-07-11T13:50:00.000Z","tags":null},"headers":[],"relativePath":"src/11408/11408.md","filePath":"src/11408/11408.md"}'),o={name:"src/11408/11408.md"},s=r('<h1 id="_11408通关之路" tabindex="-1">11408通关之路 <a class="header-anchor" href="#_11408通关之路" aria-label="Permalink to &quot;11408通关之路&quot;">​</a></h1><h2 id="写在前面" tabindex="-1">写在前面 <a class="header-anchor" href="#写在前面" aria-label="Permalink to &quot;写在前面&quot;">​</a></h2><p>这篇文章主要讲我的考研上岸经历以及一些可能不是很有用的经验总结，希望能够帮助到那些打算尝试加入11408考研大军的有志之士。会从我自己的个人基础水平情况、考研过程中的一些方法论和思想基础以及后面考研之后的事情想法。写这篇文章的时候其实已经犹豫了很久，我觉得还是挺有必要对于自己在整个过程之中进行一些记载的，一方面能稀释一下的害怕和担心，更加专注于整过程，另外一个方面是希望成为自己以后可以成功上岸时候满足一下自己的分享欲，忘记了很多细节就会给出一些大体的深入骨髓的意见，另一方面去真正接触研究生这个圈子会给你的经验总结带来一些不一样的思考。</p><h2 id="基本情况" tabindex="-1">基本情况 <a class="header-anchor" href="#基本情况" aria-label="Permalink to &quot;基本情况&quot;">​</a></h2><p>计算机科学技术专业专科，考研也继续选择计算机的学硕</p><p>因为没什么其他想法加上本专业优势。专业课就是考前划重点ppt的水平，考研科目基本上是零散学头学起，但是11408（英一数一计算机学科综合）是考研计算机仅次于912的存在，数据量还是很大的，再加上摆烂还没有对考研有一个非常具体的概念。</p><p>思考了一下，对数学和专业课进行刷题战术。</p><p>猫猫的方法论地址：</p><p><a href="https://mashiro.link/2023/10/01/road-to-11408/" target="_blank" rel="noreferrer">https://mashiro.link/2023/10/01/road-to-11408/</a></p>',9),n=[s];function _(i,c,l,p,h,d){return t(),e("div",null,n)}const u=a(o,[["render",_]]);export{f as __pageData,u as default};
