import{_ as r,c as a,ag as t,o as i}from"./chunks/framework.wRLzwZz-.js";const B=JSON.parse('{"title":"","description":"","frontmatter":{"outline":"deep"},"headers":[],"relativePath":"src/note/GIS.md","filePath":"src/note/GIS.md"}'),o={name:"src/note/GIS.md"};function n(m,e,l,E,f,_){return i(),a("div",null,e[0]||(e[0]=[t('<p>GIS系统</p><p>GIS系统即<a href="https://baike.baidu.com/item/%E5%9C%B0%E7%90%86%E4%BF%A1%E6%81%AF%E7%B3%BB%E7%BB%9F/171830?fromModule=lemma_inlink" target="_blank" rel="noreferrer">地理信息系统</a>（GIS ，geographic information system）是随着地理科学、<a href="https://baike.baidu.com/item/%E8%AE%A1%E7%AE%97%E6%9C%BA%E6%8A%80%E6%9C%AF/1127562?fromModule=lemma_inlink" target="_blank" rel="noreferrer">计算机技术</a>、<a href="https://baike.baidu.com/item/%E9%81%A5%E6%84%9F%E6%8A%80%E6%9C%AF/1697262?fromModule=lemma_inlink" target="_blank" rel="noreferrer">遥感技术</a>和<a href="https://baike.baidu.com/item/%E4%BF%A1%E6%81%AF%E7%A7%91%E5%AD%A6/1296011?fromModule=lemma_inlink" target="_blank" rel="noreferrer">信息科学</a>的发展而发展起来的一个学科。在计算机发展史上，<a href="https://baike.baidu.com/item/%E8%AE%A1%E7%AE%97%E6%9C%BA%E8%BE%85%E5%8A%A9%E8%AE%BE%E8%AE%A1/1376358?fromModule=lemma_inlink" target="_blank" rel="noreferrer">计算机辅助设计</a>技术（CAD）的出现使人们可以用计算机处理像图形这样的数据，<a href="https://baike.baidu.com/item/%E5%9B%BE%E5%BD%A2%E6%95%B0%E6%8D%AE/5199438?fromModule=lemma_inlink" target="_blank" rel="noreferrer">图形数据</a>的标志之一就是<a href="https://baike.baidu.com/item/%E5%9B%BE%E5%BD%A2%E5%85%83%E7%B4%A0/5199477?fromModule=lemma_inlink" target="_blank" rel="noreferrer">图形元素</a>有明确的位置坐标，不同图形之间有各种各样的<a href="https://baike.baidu.com/item/%E6%8B%93%E6%89%91%E5%85%B3%E7%B3%BB/5199514?fromModule=lemma_inlink" target="_blank" rel="noreferrer">拓扑关系</a>。简单地说，拓扑关系指图形元素之间的<a href="https://baike.baidu.com/item/%E7%A9%BA%E9%97%B4%E4%BD%8D%E7%BD%AE/2291427?fromModule=lemma_inlink" target="_blank" rel="noreferrer">空间位置</a>和连接关系。简单的图形元素如点、线、<a href="https://baike.baidu.com/item/%E5%A4%9A%E8%BE%B9%E5%BD%A2/10076694?fromModule=lemma_inlink" target="_blank" rel="noreferrer">多边形</a>等；点有坐标（x, y）；线可以看成由无数点组成，线的位置就可以表示为一系列坐标对（x1, y1），（x2, y2），……（xn, yn）；平面上的多边形可以认为是由闭合曲线形成范围。图形元素之间有多种多样的相互关系，如一个点在一<a href="https://baike.baidu.com/item/%E6%9D%A1%E7%BA%BF/4271152?fromModule=lemma_inlink" target="_blank" rel="noreferrer">条线</a>上或在一个多边形内，一条线穿过一个多边形等等。在实际应用中，一个地理信息系统要管理非常多、非常复杂的数据，可能有几万个多边形，几万条线，上万个点，还要计算和管理它们之间的各种复杂的<a href="https://baike.baidu.com/item/%E7%A9%BA%E9%97%B4%E5%85%B3%E7%B3%BB/2223243?fromModule=lemma_inlink" target="_blank" rel="noreferrer">空间关系</a>……。</p>',2)]))}const k=r(o,[["render",n]]);export{B as __pageData,k as default};
